/*
 * action types
 */

export const SEARCH_QUERY = 'SEARCH_QUERY';
export const SEARCH_LIST_RESULT = 'SEARCH_LIST_RESULT';


/*
 * action creators
 */

export const saveSearchQuery = query => {
    return { 
        type: SEARCH_QUERY, 
        payload: query 
    };
}

export const saveSearchList = list => dispatch => {
    return { 
        type: SEARCH_LIST_RESULT, 
        payload: list 
    };
}
