import React, { Component } from 'react';
import PropTypes from 'prop-types';
import '../assests/List.css';

class List extends Component {
    renderCard = (list) => {
        const { saveFaviourtesList } = this.props;
        const { Title, Year, Poster } = list;

        return (
            <div key={Title} className = "card">
                <div className = "card-img">
                    <img alt='loading..' src={ Poster } />
                </div>
                <div className = "card-info">{ Title }</div>
                <div className = "card-info">{ Year }</div>
                <button 
                    style={{fontSize: '24px'}}
                    onClick= {() => saveFaviourtesList}
                > 
                    <i class='fas fa-star'></i>
                </button>
            </div>
        );
    };

    render() {
        const { searchList } = this.props;

        return (
            <div className="card-container">
                { searchList.map(list => this.renderCard(list)) }
                {/* { this.renderCard(searchList[0]) } */}
            </div>
        );
    }
}

List.propTypes = {
    searchList: PropTypes.array,
    saveFaviourtesList: PropTypes.func
}

export default List;
