import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { removeFaviourtesList } from '../redux/actions/saveSearchAction';
import '../assests/Card.css';

class Card extends Component {
    render() {
        const { removeFaviourtesList, list = {} } = this.props;
        const { Title, Year, Poster } = list;

        return (
            <div key={Title} className = "card">
                <button 
                    className="fav-btn"
                    style={{fontSize: '24px'}}
                    onClick={() => removeFaviourtesList(list)}
                > 
                    Add favourite
                </button>
                <div className = "card-img">
                    <img alt='loading..' src={ Poster } />
                </div>
                <div className = "card-info">{ Title }</div>
                <div className = "card-info">{ Year }</div>
            </div>
        );
    }
}

const mapDispatchToProps = dispatch => ({
    removeFaviourtesList: list => dispatch(removeFaviourtesList(list)),
});

Card.propTypes = {
    list: PropTypes.object,
}

export default connect(
    null,
    mapDispatchToProps
)(Card);
