const initialState = {
    searchValue: "",
    searchList: []
}

const saveSearch = (state = initialState, { type, payload }) => {
    switch (type) {
        case 'SEARCH_QUERY':
            console.log("in reducer");
            return {
                ...state,
                searchValue: payload
            }

        case 'SEARCH_LIST_RESULT':
            return {
                ...state,
                searchList: payload
            }

        default:
            return state
    }
  }

  export default saveSearch;
