import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Card from './Card';

class List extends Component {
    renderCard = (list, addContent) => {
        return <Card key={list.Title} list={list} addContent={addContent} />
    };

    render() {
        const { searchList = [], favourites = [], searchQuery } = this.props;

        return (
            <div className="card-container">
                { searchQuery && !searchList.length && 'No results found. Search again...' }
                { searchList.map(list => {
                    const addContent = favourites.filter(({ Title }) => Title === list.Title);

                    return this.renderCard(list, !addContent.length);
                }) 
                }
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        searchQuery: state.searchQuery,
        searchList: state.searchList,
        favourites: state.favourites
    };
}

List.propTypes = {
    searchQuery: PropTypes.string,
    searchList: PropTypes.array,
    saveFaviourtesList: PropTypes.func
}

export default connect(
    mapStateToProps
)(List);
