import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux'
import PropTypes from 'prop-types'
import { getMyShowURL } from "../constants/index";
import { saveSearchQuery, saveSearchList } from '../actions/saveSearchAction';
import '../assests/Search.css';

class Search extends Component {
    constructor(props) {
        super(props);

        this.state = {
            searchValue: ""
        };
    }

    handleOnChange = event => {
        this.setState({ searchValue: event.target.value });
    };

    handleSearch = () => {
        const { saveSearchQuery } = this.props;
        const { searchValue } = this.state;

        saveSearchQuery(saveSearchQuery);
        this.fetchData(searchValue);
    }

    fetchData = searchInput => {
        const { saveSearchList } = this.props;
        const searchUrl = getMyShowURL(searchInput);

        fetch(searchUrl)
            .then(response => {
                return response.json();
            })
            .then(({ Search }) => {
                saveSearchList(Search);
                console.log(Search);
            });
    };

    render() {
        const { searchQuery, searchList } = this.props;

        return (
            <Fragment>
                <input 
                    name="text" 
                    type="text" 
                    placeholder="Search Movie, Series, Episodes" 
                    onChange={event => this.handleOnChange(event)}
                    value={this.state.searchValue}
                />
                <button onClick={this.handleSearch}>Search</button>

                <h1>{searchQuery}</h1>
                <h1>{searchList}</h1>
            </Fragment>
        );
    }
}

const mapStateToProps = state => {
    console.log("state" + state.stringify());
    return {
      searchQuery: state.searchQuery,
      searchList: state.searchList
    };
}


const mapDispatchToProps = dispatch => ({
    saveSearchQuery: query => dispatch(saveSearchQuery(query)),
    saveSearchList: list => dispatch(saveSearchList(list)),
});

Search.propTypes = {
    searchQuery: PropTypes.string,
    searchList: PropTypes.array,
    saveSearchQuery: PropTypes.func,
    saveSearchList: PropTypes.func,
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Search);
