import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import '../assests/List.css';

class MyContent extends Component {
    renderCard = list => {
        const { Title, Year, Poster } = list;

        return (
            <div key={Title} className = "card">
                <div className = "card-img">
                    <img alt='loading..' src={ Poster } />
                </div>
                <div className = "card-info">{ Title }</div>
                <div className = "card-info">{ Year }</div>
                <button 
                    style={{fontSize: '24px'}}
                    onClick={saveFaviourtesList(list)}
                > 
                    <i class='fas fa-star'></i>
                </button>
            </div>
        );
    };

    render() {
        const { favourites = [] } = this.props;

        return (
            <div className="card-container">
                { favourites.map(list => this.renderCard(list)) }
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        favourites: state.favourites
    };
}

MyContent.propTypes = {
    favourites: PropTypes.array
}

export default connect(
    mapStateToProps
)(MyContent);
