const initialState = {
    searchValue: "",
    searchList: []
}

const saveSearch = (state = initialState, { type, payload }) => {
    console.log("in reducer" + type + payload);

    switch (type) {
        case 'SEARCH_QUERY':
            console.log("in reducer q");
            return {
                ...state,
                searchValue: payload
            };

        case 'SEARCH_LIST_RESULT':
            console.log("in reducer l");
            return {
                ...state,
                searchList: payload
            };

        default:
            return state;
    }
  }

  export default saveSearch;
