/*
 * action types
 */

export const SEARCH_QUERY = 'SEARCH_QUERY';
export const SEARCH_LIST_RESULT = 'SEARCH_LIST_RESULT';


/*
 * action creators
 */

export function saveSearchQuery(query) {
    return dispatch({ 
        type: SEARCH_QUERY, 
        payload: query 
    });
}

export function saveSearchList(list) {
    return dispatch({ 
        type: SEARCH_LIST_RESULT, 
        payload: list 
    });
}
