import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Card from './Card';
import { removeFaviourtesList } from '../redux/actions/saveSearchAction';

class MyContent extends Component {
    renderCard = list => {
        return <Card list={list}/>
    };

    render() {
        const { favourites = [] } = this.props;

        return (
            <div className="card-container">
                { 
                    favourites.length
                        ? favourites.map(list => this.renderCard(list)) 
                        : <h3>No content added to favourites</h3>
                }
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        favourites: state.favourites
    };
}

const mapDispatchToProps = dispatch => ({
    removeFaviourtesList: list => dispatch(removeFaviourtesList(list)),
});

MyContent.propTypes = {
    favourites: PropTypes.array,
    removeFaviourtesList: PropTypes.func
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(MyContent);
