import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { removeFaviourtesList } from '../redux/actions/saveSearchAction';
import FontAwesome from 'react-fontawesome';
import '../assests/List.css';

class MyContent extends Component {
    renderCard = list => {
        const { removeFaviourtesList } = this.props;
        const { Title, Year, Poster } = list;

        return (
            <div key={Title} className = "card">
                <div className = "card-img">
                    <img alt='loading..' src={ Poster } />
                </div>
                <div className = "card-info">{ Title }</div>
                <div className = "card-info">{ Year }</div>
                <button 
                    style={{fontSize: '24px'}}
                    onClick={() => removeFaviourtesList(list)}
                > 
                    <FontAwesomeIcon icon="coffee" />
                </button>
            </div>
        );
    };

    render() {
        const { favourites = [] } = this.props;

        return (
            <div className="card-container">
                { 
                    favourites.length
                        ? favourites.map(list => this.renderCard(list)) 
                        : <h3>No content added to favourites</h3>
                }
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        favourites: state.favourites
    };
}

const mapDispatchToProps = dispatch => ({
    removeFaviourtesList: list => dispatch(removeFaviourtesList(list)),
});

MyContent.propTypes = {
    favourites: PropTypes.array,
    removeFaviourtesList: PropTypes.func
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(MyContent);
