import React, { Component } from 'react';
import PropTypes from 'prop-types';
import '../assests/List.css';

class List extends Component {
    renderCard = ({ Title, Year, Poster }) => {
        return (
            <div className = "card">
                <img className = "card-img" alt='' src={ Poster } />
                <div className = "card-info">{ Title }</div>
                <div className = "card-info">{ Year }</div>
            </div>
        );
    };

    render() {
        const { searchList } = this.props;

        return (
            <div className="card-container">
                searchList.map(list => {
                    this.renderCard(list)
                })
                { this.renderCard(searchList[0]) }
            </div>
        );
    }
}

List.propTypes = {
    searchList: PropTypes.array
}

export default List;
