import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Card from './Card';
import { saveFaviourtesList } from '../redux/actions/saveSearchAction';

class List extends Component {
    renderCard = list => {
        return <Card key={list.Title} list={list} onClick={saveFaviourtesList} />
    };

    render() {
        const { searchList = [] } = this.props;

        return (
            <div className="card-container">
                { searchList.map(list => this.renderCard(list)) }
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
      searchList: state.searchList
    };
}

const mapDispatchToProps = dispatch => ({
    saveFaviourtesList: list => dispatch(saveFaviourtesList(list)),
});

List.propTypes = {
    searchList: PropTypes.array,
    saveFaviourtesList: PropTypes.func
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(List);
